import java.util.*;import java.util.stream.*;class C extends Stack<Integer>{static int D=12;static char g[]=new char[D*D];public static void main(String[] a){String c[]=a[0].split(",");int i=0;for(String x:c){g[i++]=x.charAt(0);};C r=null;i=0;for(int _:g){C s=s(i++);r=((r==null||s.size()<r.size())&&s.size()>0)?s:r;}System.out.println(r.size()+"=>"+r.stream().map(x->"("+x%D+","+x/D+")").collect(Collectors.joining(",")));}static C s(int i){C c=new C();if(g[i]=='X'){c.add(i);g[i]=0;int x=i%D, y=i/D;for(int a=x>0?-1:0;a<(x<(D-1)?2:1);a++)for(int b=y>0?-1:0;b<(y<(D-1)?2:1);b++)c.addAll(s(i+a+D*b));}return c;}}