import java.nio.file.*;import java.util.*;import java.util.stream.*;class F extends Stack<Integer>{static int q=0,D=12;static char g[]=new char[D*D];public static void main(String[]a)throws Throwable{Files.lines(Paths.get(a[0])).forEach(p->{for(String s:p.split(","))g[q++]=s.charAt(0);});F r=null;q=0;for(int i:g){F s=s(q++);r=((r==null||s.size()<r.size()) && s.size()>0)?s:r;}System.out.println(r.size()+"=>"+r.stream().map(x->"("+x%D+","+x/D+")").collect(Collectors.joining(",")));}static F s(int i){F f=new F();if(g[i]=='X'){f.add(i);g[i]=0;int x=i%D,y=i/D;for(int a=x>0?-1:0;a<(x<(D-1)?2:1);a++)for(int b=y>0?-1:0;b<(y<(D-1)?2:1);b++)f.addAll(s(i+a+D*b));}return f;}}